import React, { useState, useEffect, useRef } from 'react';

const Chat: React.FC = () => {
  const [messages, setMessages] = useState<Array<{ username: string, content: string }>>([]);
  const [input, setInput] = useState('');
  const wsRef = useRef<WebSocket | null>(null);

  const username = localStorage.getItem('username') || '???';

  useEffect(() => {
    // connect to WebSocket
    const ws = new WebSocket('ws://localhost:8080/ws');
    wsRef.current = ws;

    ws.onopen = () => {
      console.log('[WS] connected');
      // send an initial "join" event or something
      ws.send(JSON.stringify({ type: 'join', username }));
    };

    ws.onmessage = (evt) => {
      const data = JSON.parse(evt.data);
      if(data.type === 'chat') {
        // { username, content }
        setMessages(prev => [...prev, { username: data.username, content: data.content }]);
      }
    };

    ws.onclose = () => {
      console.log('[WS] closed');
    };

    return () => {
      ws.close();
    };
  }, [username]);

  const handleSend = () => {
    if(!wsRef.current) return;
    const msg = {
      type: 'chat',
      username,
      content: input
    };
    wsRef.current.send(JSON.stringify(msg));
    setInput('');
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Real-Time Chat as {username}</h2>
      <div style={{ border: '1px solid #ccc', height: '300px', overflowY: 'auto', marginBottom: '1rem' }}>
        {messages.map((m, idx) => (
          <div key={idx} style={{ margin: '0.5rem 0' }}>
            <strong>{m.username}:</strong> {m.content}
          </div>
        ))}
      </div>
      <input
        value={input}
        onChange={e => setInput(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={handleSend}>Send</button>
    </div>
  );
};

export default Chat;
