// messageService.ts
// For sending and fetching messages

export async function sendMessage(token: string, senderId: number, content: string) {
    const response = await fetch("http://localhost:8080/api/messages", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        // Optionally pass token if you want secure routes
        "Authorization": `Bearer ${token}`,
      },
      body: JSON.stringify({ senderId, content }),
    });
    if (!response.ok) {
      throw new Error(`Send message failed: ${response.statusText}`);
    }
    return await response.json();
  }
  
  export async function fetchMessages(token: string) {
    const response = await fetch("http://localhost:8080/api/messages", {
      headers: {
        "Authorization": `Bearer ${token}`,
      },
    });
    if (!response.ok) {
      throw new Error(`Fetch messages failed: ${response.statusText}`);
    }
    return await response.json();
  }
