// src/pages/Chat.tsx
import React, { useState, useEffect, useRef } from 'react';
import Header from '../components/Header';
import ChatList from '../components/ChatList';
import { sendMessage } from '../services/messageService';
import FileUpload from '../components/FileUpload';

const Chat: React.FC = () => {
  const [message, setMessage] = useState('');
  const [allMessages, setAllMessages] = useState<any[]>([]);
  const eventSourceRef = useRef<EventSource | null>(null);

  const token = localStorage.getItem('token') || '';
  const userId = parseInt(localStorage.getItem('userId') || '0');

  // Connect SSE once on mount
  useEffect(() => {
    if (!token) return;

    // Start SSE
    const es = new EventSource(`http://localhost:8080/api/sse?token=${token}`);
    eventSourceRef.current = es;

    // When server sends a message
    es.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data); 
        // data = { id, sender_id, content, created_at }
        // We’ll add this new message to the array
        setAllMessages(prev => [...prev, data]);
      } catch (error) {
        console.error('SSE parse error', error);
      }
    };

    es.onerror = (err) => {
      console.error('SSE error:', err);
    };

    // Cleanup
    return () => {
      es.close();
    };
  }, [token]);

  // Send message normally via fetch
  const handleSend = async () => {
    if (!message) return;
    try {
      await sendMessage(token, userId, message);
      setMessage('');
      // We do NOT do a fetchMessages here. Instead, SSE automatically pushes new messages.
    } catch (err) {
      console.error('Send message error:', err);
    }
  };

  return (
    <div>
      <Header />
      <div style={{ display: 'flex', height: '80vh' }}>
        <ChatList />
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
          <div style={{ flex: 1, border: '1px solid #ccc', padding: '1rem', overflowY: 'auto' }}>
            <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
              {allMessages.map(msg => (
                <li key={msg.id} style={{ marginBottom: '0.5rem' }}>
                  <strong>User {msg.sender_id}:</strong> {msg.content}
                </li>
              ))}
            </ul>
          </div>
          <div style={{ padding: '1rem' }}>
            <input
              value={message}
              onChange={e => setMessage(e.target.value)}
              placeholder="Type message..."
            />
            <button onClick={handleSend}>Send</button>

            {/* OPTIONAL: We can also place the FileUpload component here */}
            <FileUpload />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
